item.content:encoded:
query GetSeedNode($id: ID!, $uri: String! = "", $asPreview: Boolean = false) {
  ... on RootQuery @skip(if: $asPreview) {
    nodeByUri(uri: $uri) {
      __typename
      ...GetNode
    }
    __typename
  }
  ... on RootQuery @include(if: $asPreview) {
    contentNode(id: $id, idType: DATABASE_ID, asPreview: true) {
      __typename
      ...GetNode
    }
    __typename
  }
}

fragment GetNode on UniformResourceIdentifiable {
  __typename
  uri
  id
  ...DatabaseIdentifier
  ...ContentType
  ...User
  ...TermNode
  ...ContentNode
  ...MediaItem
  ...Page
}

fragment DatabaseIdentifier on DatabaseIdentifier {
  databaseId
  __typename
}

fragment MediaItem on MediaItem {
  id
  mimeType
  __typename
}

fragment ContentType on ContentType {
  name
  isFrontPage
  isPostsPage
  __typename
}

fragment Page on Page {
  isFrontPage
  isPostsPage
  __typename
}

fragment TermNode on TermNode {
  isTermNode
  slug
  taxonomyName
  __typename
}

fragment ContentNode on ContentNode {
  isContentNode
  slug
  contentType {
    node {
      name
      __typename
    }
    __typename
  }
  template {
    templateName
    __typename
  }
  __typename
}

fragment User on User {
  name
  userId
  databaseId
  __typename
}

query GetPage($databaseId: ID!, $asPreview: Boolean = false, $headerLocation: MenuLocationEnum!, $footerLocation: MenuLocationEnum!) {
  page(id: $databaseId, idType: DATABASE_ID, asPreview: $asPreview) {
    title
    ncPageMeta {
      isFullWithPage
      __typename
    }
    featuredImage {
      node {
        altText
        sourceUrl
        __typename
      }
      __typename
    }
    editorBlocks(flat: true) {
      __typename
      renderedHtml
      clientId
      parentClientId
      ...NcmazFaustBlockMagazineFragment
      ...NcmazFaustBlockTermsFragment
      ...NcmazFaustBlockCtaFragment
      ...NcmazFaustBlockGroupFragment
      ...CoreColumnsFragment
      ...CoreColumnFragment
    }
    __typename
  }
  generalSettings {
    ...NcgeneralSettingsFieldsFragment
    __typename
  }
  primaryMenuItems: menuItems(where: {location: $headerLocation}, first: 80) {
    nodes {
      ...NcPrimaryMenuFieldsFragment
      __typename
    }
    __typename
  }
  footerMenuItems: menuItems(where: {location: $footerLocation}, first: 40) {
    nodes {
      ...NcFooterMenuFieldsFragment
      __typename
    }
    __typename
  }
}

