query GetPage($databaseId: ID!, $asPreview: Boolean = false, $headerLocation: MenuLocationEnum!, $footerLocation: MenuLocationEnum!) {
  page(id: $databaseId, idType: DATABASE_ID, asPreview: $asPreview) {
    title
    featuredImage {
      node {
        altText
        sourceUrl
        __typename
      }
      __typename
    }
    editorBlocks(flat: true) {
      __typename
      renderedHtml
      clientId
      parentClientId
      ...NcmazFaustBlockMagazineFragment
      ...NcmazFaustBlockTermsFragment
      ...NcmazFaustBlockCtaFragment
      ...NcmazFaustBlockGroupFragment
      ...CoreColumnsFragment
      ...CoreColumnFragment
    }
    __typename
  }
  generalSettings {
    ...NcgeneralSettingsFieldsFragment
    __typename
  }
  primaryMenuItems: menuItems(where: {location: $headerLocation}, first: 50) {
    nodes {
      ...NcPrimaryMenuFieldsFragment
      __typename
    }
    __typename
  }
  footerMenuItems: menuItems(where: {location: $footerLocation}, first: 40) {
    nodes {
      ...NcFooterMenuFieldsFragment
      __typename
    }
    __typename
  }
}

